import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import './Header.css';

const Header = (props) =>{
	console.log("........")
	return(
		<div className='row'>
			<div className="col-12 col-sm-12 col-md-12 col-lg-12 col-xl-12 app_header">
			{props.showBackBtn 
				?
					<FontAwesomeIcon className="back_to_prev" icon={faArrowLeft} style={{ color: '#fff', marginRight: '15px' }} />
				:
					null
			}
				{props.heading}
			</div>
		</div>
	)
}

export default Header;

