import React, { useState } from "react";
import { useParams } from "react-router-dom";
import "./MovieDetails.css";

const MovieDetails = (props) =>{
	const params = useParams();
	console.log("movie props id => ",params.id)

	const [movie, setMovie] = useState(       
        {

            "year": 2013,
	        	"title": "2 Guns",	        	
            "directors": ["Baltasar Kormakur"],
            "release_date": "2013-07-30T00:00:00Z",
            "rating": 7,
            "genres": [
                "Action",
                "Comedy",
                "Crime",
                "Drama",
                "Thriller"
            ],
            "image_url": "https://m.media-amazon.com/images/M/MV5BNTQ5MTgzNDg4OF5BMl5BanBnXkFtZTcwMjAyODEzOQ@@._V1_SX400_.jpg",
            "description": "A DEA agent and a naval intelligence officer find themselves on the run after a botched attempt to infiltrate a drug cartel. While fleeing, they learn the secret of their shaky alliance: Neither knew that the other was an undercover agent.",
            "rank": 62,
            "movie_duration": 6540,
            "actors": [
                "Denzel Washington",
                "Mark Wahlberg",
                "Paula Patton"
            ]	        
        }
    );

	return(
		<div className="col-12 col-sm-12 col-md-12 col-lg-12 col-xl-12">
			<div className="row">
				<div className="movie_title col-12 col-sm-12 col-md-12 col-lg-12 col-xl-12">
					{movie.title}
				</div>
				<div className="col-12 col-sm-6 col-md-3 col-lg-3 col-xl-3">
					<img src={movie.image_url} alt='movie'></img>					
				</div>				
				<div className="col-12 col-sm-6 col-md-9 col-lg-9 col-xl-9">
					<div className="">{movie.release_date}</div>		
					<div className="">{movie.movie_duration}</div>		
					<div className="">{movie.rating}</div>		
					<div className=""><button>Add to Favorit</button></div>
				</div>
				<div className="col-12 col-sm-12 col-md-12 col-lg-12 col-xl-12">
					{movie.description}				
				</div>
				<hr/>
				<div className="col-12 col-sm-12 col-md-12 col-lg-12 col-xl-12">
					{movie.actors.map((actor, index) => {
						<div key={"actor-"+index}className="col-12 col-sm-12 col-md-12 col-lg-12 col-xl-12">
							{actor}
						</div>
					})}		
				</div>						
			</div>

		</div>
	)
}

export default MovieDetails;
