import React, { useState } from "react";
import { useParams } from "react-router-dom";
import "./MovieDetails.css";

const MovieDetails = (props) =>{
	const params = useParams();
	console.log("movie props id => ",params.id)

	const [movie, setMovie] = useState(       
        {

            "movie_title": "Star Wars: Episode IV - A New Hope",
            "release_year": "1977",
            "imdbID": "tt0076759",
            "movie_duration": "120",
            "movie_trailers": [],
            "movie_rating" : 9,
            "movie_poster": "https://m.media-amazon.com/images/M/MV5BNzVlY2MwMjktM2E4OS00Y2Y3LWE3ZjctYzhkZGM3YzA1ZWM2XkEyXkFqcGdeQXVyNzkwMjQ5NzM@._V1_SX300.jpg",
            "movie_description" : "A movie or film is a type of visual communication scenario which uses moving pictures and sound to tell stories or teach people something. Most people watch (view) movies as a type of entertainment or a way to have fun. For some people, fun movies can mean movies that make them laugh, while for others it can mean movies that make them cry, or feel afraid."
        }
    );

	return(
		<div className="col-12 col-sm-12 col-md-12 col-lg-12 col-xl-12">
			<div className="row">
				<div className="col-12 col-sm-12 col-md-12 col-lg-12 col-xl-12">
					{movie.movie_title}
				</div>
				<div className="col-12 col-sm-6 col-md-3 col-lg-3 col-xl-3">
					<img src={movie.movie_poster} alt='movie'></img>					
				</div>				
				<div className="col-12 col-sm-6 col-md-9 col-lg-9 col-xl-9">
					<div className="">{movie.release_year}</div>		
					<div className="">{movie.movie_duration}</div>		
					<div className="">{movie.movie_rating}</div>		
					<div className=""><button>Add to Favorit</button></div>
				</div>
				<div className="col-12 col-sm-12 col-md-12 col-lg-12 col-xl-12">
					<img src={movie.movie_description} alt='movie'></img>					
				</div>
				<hr/>
				<div className="col-12 col-sm-12 col-md-12 col-lg-12 col-xl-12">
					{movie.movie_trailers.map((trailer, index) => {
						<div key={"trailer-"+index}className="col-12 col-sm-12 col-md-12 col-lg-12 col-xl-12">
							<video src={trailer} width="320" height="240" controls />
						</div>
					})}		
				</div>						
			</div>

		</div>
	)
}

export default MovieDetails;
